from kivy.core.text import LabelBase
from re import MULTILINE
from kivy.app import App
from kivy.core import text
from kivy.uix.gridlayout import GridLayout
from kivy.uix.label import Label
from kivy.uix.image import Image
from kivy.uix.button import Button
from kivy.uix.textinput import TextInput

class SayHello(App):
    def callback(self, instens):
        self.greeting.text = "hellow " + self.user.text + "!"

    def build(self):
        self.window = GridLayout()
        self.window.cols = 1

        self.window.size_hint = (0.6, 0.9)
        self.window.pos_hint = {"center_x":0.5, "center_y":0.5}
        # LabelBase.register(name="Arial", fn_regular="Arial.ttf")


        #add widgets to window
        self.window.add_widget(Image(source = "data:image/jpeg;base64,"))
        
        self.greeting = Label(
                            text = " whats your name? ",
                            font_size = 18,
                            font_name = "ariel",
                            color = "#00FFCE"
                            )
        self.window.add_widget(self.greeting)
        
        self.user = TextInput(
                            multiline = False,
                            padding_y =(20,20),
                            size_hint = (1, 0.25),
                            )
        self.window.add_widget(self.user)

        self.button = Button(text = "greet", size_hint = (1, 0.25), bold =True, background_color = "#00FFCE")
        self.button.bind(on_press = self.callback)
        self.window.add_widget(self.button)

        return self.window

    
        
        

if __name__ == "__main__":
    SayHello().run()
